        private string GetRandomSampling()
        {
            var sampledAliases = new List<string>();
            
            var guys = new List<Alias>() {aggressivelyFantastic, forts, locations, compositionTypes, artStyles, weatherPhenomena };
            guys = guys.OrderBy(x => Random.Shared.Next()).ToList();
            foreach (var alias in guys)
            {
                var val = alias.GetRandomAlias(true);
                if (!string.IsNullOrEmpty(val))
                {
                    sampledAliases.Add(val);
                }
                if (sampledAliases.Count >= 1)
                {
                    break;
                }
            }
            return string.Join(", ", sampledAliases);
        }

        public Task<bool> DoTransformation(PromptDetails pd)
        {
            var randoms = GetRandomSampling();
            var newVersion = $"'{pd.Prompt}' {randoms}";
            pd.ReplacePrompt(newVersion, newVersion, TransformationType.Randomizer);

            return Task.FromResult(true);
        }
    }
